(window.webpackJsonp=window.webpackJsonp||[]).push([[14],{387:function(n,h,i){"use strict";i.r(h);var t=i(8),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"khong-ngo-lai-co-nguoi-dat-lon-đi-đoi-tai-khoan-pi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#khong-ngo-lai-co-nguoi-dat-lon-đi-đoi-tai-khoan-pi"}},[n._v("#")]),n._v(" Không ngờ lại có người dắt lợn đi đổi tài khoản Pi")]),n._v(" "),i("p",[n._v("Nhìn vào bức ảnh do Pioneer cung cấp, một con lợn Trung Quốc có một bông hoa màu trắng, cơ thể khỏe mạnh và thị lực cả hai mắt là 1,5. Tính theo giá thị trường nó có trị giá 3565 RMB. Tài khoản Pi được trao đổi có tổng 1897 Pi. Bởi vì không mời người khác tham gia Pi nên nhóm là 1/1, khi lên xe Mainnet hoàn toàn có thể giữ được gần đủ số Pi đó.")]),n._v(" "),i("p",[i("img",{attrs:{src:"/assets/img/pi-china-exchange-1.png",alt:"pinetwork"}})]),n._v(" "),i("p",[n._v("Tôi đào trong hai tháng với tốc độ 0,25 Pi mỗi giờ, được 6 Pi mỗi ngày, 360 Pi được đào và số tiền cơ sở là khoảng 140 Pi. 1500 Pi còn lại được đào với tốc độ 0,49 Pi mỗi giờ, lúc đó tối đa là 350 Pi mỗi tháng, có vẻ như sau 4 tháng đào, số Pi cơ bản còn khoảng 560. Tổng cộng 700 Pi cơ bản! Đổi một tài khoản như vậy lấy một con lợn trị giá 3565 RMB có đáng không?")]),n._v(" "),i("h1",{attrs:{id:"hinh-thu-hai-la-giao-dich-hop-đong-tuong-lai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hinh-thu-hai-la-giao-dich-hop-đong-tuong-lai"}},[n._v("#")]),n._v(" Hình thứ hai là giao dịch hợp đồng tương lai")]),n._v(" "),i("p",[n._v("Ký hợp đồng thanh toán trước 10000 RMB và chuyển khoản Alipay. Dù là giao dịch tiền mặt hay hàng đổi hàng, nhưng tóm lại nó đều có giá trị và sự đồng thuận ngày càng mạnh mẽ. Giá cả là biểu hiện của giá trị và giá trị cụ thể được trao cho thời gian mà không cần lo lắng. Với sự phát triển của Pi Network chỉ có ngày càng nhiều người dùng, hệ sinh thái càng hoàn thiện, càng gần với thành công và sự bền bỉ cuối cùng sẽ tốt hơn.")]),n._v(" "),i("p",[i("img",{attrs:{src:"/assets/img/pi-china-exchange-3.png",alt:"pinetwork"}})]),n._v(" "),i("h1",{attrs:{id:"hay-nhin-bang-cai-nhin-moi-ve-ky-nguyen-blockchain"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hay-nhin-bang-cai-nhin-moi-ve-ky-nguyen-blockchain"}},[n._v("#")]),n._v(" Hãy nhìn bằng cái nhìn mới về kỷ nguyên Blockchain")]),n._v(" "),i("p",[n._v("Kỷ nguyên dựa vào sự cường điệu, lôi kéo bằng những mánh lới quảng cáo và đánh lừa mọi người sắp trôi qua. Đã đến lúc phải có các ứng dụng Blockchain thực sự, không chỉ dừng lại ở các biểu tượng. Các Token được phát hành trên các Blockchain công khai của người khác cũng sẽ nguội dần. Dù nó có tốt đến đâu thì cũng không có kịch bản ứng dụng thương mại nào, đó là bơm thôi và chỉ duy nhất có mục đích kiếm tiền rồi bỏ chạy.")]),n._v(" "),i("p",[n._v("Để đánh giá một đồng tiền mã hóa có triển vọng phát triển hay không thì sẽ biết được nhu cầu thị trường, không có nhu cầu thì không có giá trị, làm sao có thị trường không có giá trị? Tại sao Ethereum lại đáng tiền? Do việc tạo ra các hợp đồng thông minh, nhiều Token dựa trên nó hoặc được phát hành trên Blockchain công khai của nó. Bây giờ tôi thấy rằng một Token như vậy là bình thường và vô giá trị. Điều nực cười là nhiều dự án đã bơm thổi những Token mà họ đã phát hành thành giá lên trời. Điều nực cười hơn là càng ít hiểu biết và càng ít học về Blockchain thì lại càng tin, thức tỉnh đi!")]),n._v(" "),i("p",[n._v("Nếu bạn phải làm việc nhiều và không có nhiều thời gian để đọc, tôi sẽ cho bạn biết cách dễ nhất và nhanh nhất để xem liệu một dự án Blockchain có đáng tin cậy hay không. Có ba điểm chính:")]),n._v(" "),i("p",[n._v("Đó là một Blockchain công cộng.\nDoanh nghiệp đã được đề cập.\nCộng đồng có tự trị.\nNếu không đáp ứng đủ điều kiện, có tiếng mà không có ích lợi gì thì sớm muộn gì bạn cũng sẽ leo lên và ngã xuống.")]),n._v(" "),i("h1",{attrs:{id:"giai-đoan-phat-trien-co-trat-tu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#giai-đoan-phat-trien-co-trat-tu"}},[n._v("#")]),n._v(" Giai đoạn phát triển có trật tự")]),n._v(" "),i("p",[n._v("Sự phát triển không trật tự của Blockchain từ thời kỳ hỗn loạn sẽ từ từ chuyển sang giai đoạn có trật tự. Một số người so sánh năm 2020 với năm đầu tiên của Blockchain. Bây giờ nó sẽ ở giai đoạn chuyển tiếp này. Năm 2021 sẽ là một năm bùng phát và một năm định hình lại quy tắc. Giai đoạn chuyển tiếp này không phải là một dự án nhất định hoặc một Blockchain công cộng nào đó, mà là một ngành công nghiệp hoặc thậm chí là sự chuyển đổi thời đại! Một lượng vốn lớn đã bắt đầu đổ vào những tài sản có chất lượng cao, thay vào đó thứ đang thiếu không phải là cơ hội mà chính là trí óc biết tìm kiếm và nhận biết cơ hội!")]),n._v(" "),i("p",[n._v("Hiện tại, không có hơn 30 đồng tiền mã hóa có Blockchain công khai trên thế giới và có tới 15 Blockchain công khai của các hệ điều hành cơ bản trưởng thành, nhưng những đồng tiền thực sự có thể đạt được phạm vi kinh doanh và dịch vụ, ngoại trừ Ethereum thì tiếp theo phụ thuộc vào Pi. Blockchain công khai Ethereum vẫn chưa hoàn thiện và nhiều ứng dụng bị hạn chế. Pi đã phát triển được gần hai năm, khi các dự án khác từ xuân sang thu đều suôn sẻ và biến mất giữa chừng, hiệu quả của Pi là: Xuân qua đi mà hoa vẫn còn, chim cũng không ngừng hót! Pi sẽ ở lại với chúng ta trong thời gian dài, tùy thuộc vào việc bạn có thể ở lại với mọi người hay không!")])])}),[],!1,null,null,null);h.default=c.exports}}]);